import { useState } from 'react';
import Nota from './Componentes/Nota';
import Resultado from './Componentes/Resultado';
import 'css'



function App() {

  const[nota, setNotas]=useState({'nota1':'','nota2':'', 'nota3': '' , 'nota4': ''})

  const handleSetNotas=(e)=>{
      if(e.target.getAttribute('name')=='n1'){
        setNotas({'nota1':e.target.value,'nota2':nota.nota2, 'nota3': nota.nota3 , 'nota4': nota.nota4})

      }else if(e.target.getAttribute('name')=='n2'){
        setNotas({'nota1':nota.nota1,'nota2':e.target.value, 'nota3': nota.nota3 , 'nota4': nota.nota4})

      }else if(e.target.getAttribute('name')=='n3'){
        setNotas({'nota1':nota.nota1,'nota2':nota.nota2, 'nota3': e.target.value , 'nota4': nota.nota4})

      }else if(e.target.getAttribute('name')=='n4'){
        setNotas({'nota1':nota.nota1,'nota2':nota.nota2, 'nota3': nota.nota3 , 'nota4':e.target.value})
      }

  }


return (
  <div className='caixa'>
    <Nota name={'n1'} num={1} nota={nota.nota1} setNota={handleSetNotas}/>
    <Nota name={'n2'} num={2} nota={nota.nota2} setNota={handleSetNotas}/>
    <Nota name={'n3'} num={3} nota={nota.nota3} setNota={handleSetNotas}/>
    <Nota name={'n4'} num={4} nota={nota.nota4} setNota={handleSetNotas}/>

    <Resultado somaDasNotas={parseFloat(nota.nota1) + parseFloat(nota.nota2) + parseFloat(nota.nota3) + parseFloat(nota.nota4)} />

  </div>

)

}

export default App;
