import React, { useState} from "react";


function App() {





 const[categoria,setCategoria]=useState=('')



const tabelaCarros=(cat)=>{
  return(
    <table  border='1'  style={{borderCollapse:'collapse'}}>
      <thead>
        <tr>
          <th>Categoria</th><th>preço</th><th>Modelo</th>
        </tr>
      </thead>
      <tbody>  
        {linhas(cat)}
  {console.log(cat)}
      </tbody>

    </table>
  )
}

const pesquisaCategoria =(cat,scat)=>{
  return(
    <div>
        <label>Digite uma categoria:</label>
        <input onChange={(e)=> scat(e.target.value)} value={cat} type={'text'} ></input>
    </div>
  )
}


return (
  <>
    {pesquisaCategoria(categoria,setCategoria)}
    <br></br>
    {tabelaCarros(categoria)}

  </>
)

}

export default App;
