  const[operado, setOperado]=useState(false)
  const valoresOper =[]






// COMPONENTES:
const Tela=(valor,res)=>{
  return(
    <div style={cssTela}>
      <span style={cssTelaOper}>{valor}</span>    
      <span style={cssTelaRes}>{res}</span>    
    </div>
  )
}

const Btn=(label, onClick)=>{
  return(
  <button style={cssBtn} onClick={onClick}>{label}</button>
  )
}

// FUNÇÕES:
