    setValorTela(resultado + d)
    return
  }
  if(operado){
    setValorTela(d)
    setOperado(false)
    return
  }else{

  const valorDigitadoTela = valorTela + d
  setValorTela(valorDigitadoTela)

   valoresOper.push(d)
      console.log(valoresOper);
  }
}


// console.log( valores[valores.length-1]);
// console.log( valores[valores.length-2]);


const LimparMemoria=()=>{
  setOperado(false)
  setValorTela('')
  setResultado(0)
  setACumulador(0)
}

const operacao=(oper)=>{
  if(oper == 'bs'){
    let vtela = valorTela
    vtela = vtela.substring(0,(vtela.length-1))
    setValorTela(vtela)
    setOperado(false)
    return
  }
  try{
    const r=eval(valorTela) //Calculo
    setACumulador(r)
    setResultado(r)
    setOperado(true)
  }catch{
    setResultado('ERRO')
  }

}


// ESTILOS:
const cssConteiner={
  display:'flex',
  justifyContent:'center',
  alignItens:'center',
  flexDirection:'column',
  width:'300px',
  border:'1px solid #000',
}

const cssBotoes={
  flexDirection:'row',
  flexWrap:'wrap',
}
const cssTela={
  display:'flex',
  paddingLeft:'20px',
  paddinhRight:'20px',
  justifyContent:'center',
  alignItens:'Flex-start',
  backgroundColor:'#444',
  flexDirection:'column',
  width:'280px'
}

const cssTelaOper={
  fontSize:'25px',
  color:'#fff',
  height:'20px',
}

const cssTelaRes={
  fontSize:'50px',
  color:'#fff',
}

const cssBtn={
  fontSize:'30px',
  height:'75px',
  width:'75px',
  padding:'20px',
  backgroundColor:'#000',
  color:'#fff',
  borderColor:'#000',
  textAlign:'center',
  outline:'none',
}

return (
  <>
    <div style={cssConteiner}>
      <h3>Calculadora Matemática simples</h3>
      {Tela(valorTela, resultado)}
      <div style={cssBotoes}>
        {Btn('AC', LimparMemoria)}
        {Btn('(', ()=>AddDigitoTela('('))}
        {Btn(')', ()=>AddDigitoTela(')'))}
        {Btn('/', ()=>AddDigitoTela('/'))}
        {Btn('7', ()=>AddDigitoTela('7'))}
        {Btn('8', ()=>AddDigitoTela('8'))}
        {Btn('9', ()=>AddDigitoTela('9'))}
        {Btn('*', ()=>AddDigitoTela('*'))}
        {Btn('4', ()=>AddDigitoTela('4'))}
        {Btn('5', ()=>AddDigitoTela('5'))}
        {Btn('6', ()=>AddDigitoTela('6'))}
        {Btn('-', ()=>AddDigitoTela('-'))}
        {Btn('1', ()=>AddDigitoTela('1'))}
        {Btn('2', ()=>AddDigitoTela('2'))}
        {Btn('3', ()=>AddDigitoTela('3'))}
        {Btn('+', ()=>AddDigitoTela('+'))}
        {Btn('0', ()=>AddDigitoTela('0'))}
        {Btn('.', ()=>AddDigitoTela('.'))}
        {Btn('<-',()=>operacao('bs'))}
        {Btn('=', ()=>operacao('='))}

      </div>
    </div>
  </>
)

}

export default App;
