import React, { useState} from "react";
import Fpeso from "./Componentes/Fpeso";


 const tabelaIMC=()=>{
  return(

  <table border={'1'} style={{borderCollapse:'collapse'}}>
    <thead>
      <tr>
        <th>Classificação</th>
        <th>IMC</th>
      </tr>
    </thead>
    <tbody>
      <tr>
        <td>Abaixo do peso</td>
        <td>Abaixo de 18,5</td>
      </tr>
      <tr>
        <td>Peso Normal</td>
        <td>Entre 18,5 e 24,9</td>
      </tr>
      <tr>
        <td>Sobrepeso</td>
        <td>Entre 25 e 29,9</td>
      </tr>
      <tr>
        <td>Obesidade Grau I</td>
        <td>Entre 30 e 34,9</td>
      </tr>
      <tr>
        <td>Obesidade de Grau II</td>
        <td>Entre 35 e 39,9</td>
      </tr>
      <tr>
        <td>Obesidade de Grau III ou Morbida.</td>
        <td>Abaixo de 18,5</td>
      </tr>
    </tbody>
 </table>

  )
 }

//  const fPeso=(p,setP)=>{
//    return(
//       <div>
//           <label>Peso: <input onChange={(e)=>setP(e.target.value)} value={p} type={'number'}></input></label>

//       </div>
//    )
//  }

 const fAltura=(a,setA)=>{
  return(
     <div>
         <label>altura: <input onChange={(e)=>setA(e.target.value)} value={a} type={'number'}></input></label>

     </div>
  )
}

const fCalcular=(p,a,setR,)=>{

   let calc=()=>{
    setR(p/(a*a))

  }
  return(
      <div>
        <button onClick={calc}>Calcular</button>
      </div>
  )
}

const fResultado=(r)=>{
  // let resul = Number(r)
  return(
    <div>
      <p>resultado:{Number(r).toFixed(2)}</p>

    </div>
  )
}



function App() {

  const[peso, setPeso]=useState(0)
  const[altura, setAltura]=useState(0)
  const[resultado,setResultado]=useState()


return (
  <>
  <peso p={peso} setP={setPeso}/>
  {fAltura(altura,setAltura)}
  {fCalcular(peso,altura,setResultado)}
  {fResultado(resultado)}
  {tabelaIMC()}

  </>
)

}

export default App;
