import { useState, useEffect } from 'react';
import Caixa from './Componentes/Caixa';
import Canal from './Componentes/Canal';
import Nota from './Componentes/Nota';
import Resultado from './Componentes/Resultado';




function App() {


  const[nome,setNome]=useState('')

  const salveNome=(e)=>{
    setNome(e.target.value)
  }

  const gravar=(chave,valor)=>{
    localStorage.setItem(chave,valor)
  }

  const mostrar=(chave)=>{
     const resultado = document.getElementById('resultado')
   resultado.innerHTML = localStorage.getItem(chave)
  }

return (
  <>
    <label>Digite o nome</label>
    <input onChange={(e)=>salveNome(e)} value={nome} type={'text'}></input><br></br>
    <button onClick={()=>gravar('lsNome',nome)} >GRAVAR</button>
    <button onClick={()=>mostrar('lsNome')} >MOSTRAR</button>
    <div id='resultado'></div>
  </>
)

}

export default App;
