import React from "react";

class Carro extends React.Component{
    constructor(props){
        super(props)

       this.modelo = 'HRV'

       this.state={
        ligado:false,
        velAtual:0
    }

    this.ld  = this.ligarDesligar.bind(this)

}
        ligarDesligar(){
        // this.state.ligado = true
       //this.setState({ligado: !this.state.ligado})
       this.setState(
           (state)=>({ligado: !state.ligado})
       )
    }

   acelerar(){
       this.setState(
           {velAtual:this.state.velAtual + this.props.fator}
       )
   }

    render(){
        return(
            <>
                <h1>Primeiro componete de classe</h1>
                <p>modelo: {this.modelo}</p>
                <p>ligado: {this.state.ligado ? 'Sim' : 'Nao'}</p>
                <p>velocidade Atual: {this.state.velAtual}</p>

                <button onClick={this.ld}>{this.state.ligado ? 'DESLIGAR' : 'LIGAR'}</button>

                <button onClick={()=>this.acelerar()}>ACELERAR</button>

            </>
        )
    }
}

export default Carro
