import React from "react"

class BaseClasse extends React.Component{

    constructor(props){
        super(props)

        this.state={
            canal:'CFB cursos',
            curso:'React',
            ativo: true,
        }
    }




    render(){
        return(
           <>
                <h1>Componente de classe</h1>
                <p>ativo: {this.state.ativo ? 'sim' : 'nao'}</p>
                <button onClick={()=>mudarAtivo()}>MUDAR ATIVO</button>
           </>
        )
    }

}

export default BaseClasse
