import { useState } from 'react';
import Caixa from './Componentes/Caixa';
import Canal from './Componentes/Canal';
import Nota from './Componentes/Nota';
import Resultado from './Componentes/Resultado';




function App() {

  const[nota,setNota]=useState({nota1:'',nota2:'',nota3:''})

  const handleChangeNotas =(e)=>{
    if(e.target.getAttribute('name')== 'n1'){
      setNota({nota1:e.target.value,nota2:'',nota3:''})
    }else  if(e.target.getAttribute('name')== 'n2'){ {
        setNota({nota1:nota.nota1,nota2:e.target,nota3:''})
      }
    }
  }

return (
  <>
    <Nota nota={nota.nota1} handleChangeNotas={handleChangeNotas} name={'n1'} num={1}/>
    <Nota nota={nota.nota2} handleChangeNotas={handleChangeNotas} name={'n2'} num={2}/>
    <Nota nota={nota.nota3} handleChangeNotas={handleChangeNotas} name={'n3'} num={3}/>
    <Resultado total={nota.nota1 + nota.nota2 + nota.nota3}/>
  </>
)

}

export default App;
