import React, { useCallback, useState } from "react";
import Globais from "./Componentes/Globais";



function App() {

const[resumo, setResumo]=useState(Globais.resumo)

const gravarResumo=()=>{
  Globais.resumo = resumo
}

const mostrarResumo=()=>{
  alert(resumo)
}

return (
  <>
  <p>{'canal: ' + Globais.canal}</p>
  <p>{'curso: ' + Globais.curso}</p>
  <p>{'ano: ' + Globais.ano}</p>
  <hr></hr>
  <input type={'text'} onChange={(e)=>setResumo(e.target.value)} value={resumo}></input>

  <button onClick={()=>gravarResumo}>gravar</button>
  <button onClick={()=>mostrarResumo}>mostrar</button>






  </>
)

}

export default App;
