function App() {

  const[nota,setNota]=useState({nota1:'',nota2:'',nota3:''})

  const handleChangeNotas =(e)=>{
    if(e.target.getAttribute('name')== 'n1'){
      console.log();
    }
  }

return (
  <>
    <Nota handleChangeNotas={handleChangeNotas} name={'n1'} num={1}/>
    <Nota handleChangeNotas={handleChangeNotas} name={'n2'} num={2}/>
    <Nota handleChangeNotas={handleChangeNotas} name={'n3'} num={3}/>
    <Resultado/>
  </>
)

}

export default App;
