import React, { useState} from "react";




function App() {

  const[valorTela, setValorTela]=useState('')
  const[resultado, setResultado]=useState(0)
  const[acumulador, setACumulador]=useState(0)
  const[operado, setOperado]=useState(false)

// COMPONENTES:
const Tela=(valor,res)=>{
  return(
    <div style={cssTela}>
      <span style={cssTelaOper}>{valor}</span>    
      <span style={cssTelaRes}>{res}</span>    
    </div>
  )
}

const Btn=(label, onClick)=>{
  return(
  <button style={cssBtn} onClick={onClick}>{label}</button>
  )
}

// FUNÇÕES:
const AddDigitoTela=(d)=>{
  if((d=='+' || d=='-' || d=='*' || d=='/') && operado ){
    console.log("+-*/");
    setOperado(false)
    setValorTela(resultado + d)
    return
  }
  if(operado){
    setValorTela(d)
    setOperado(false)
    return
  }
  const valorDigitadoTela = valorTela + d
  setValorTela(valorDigitadoTela)
}

const LimparMemoria=()=>{
  setOperado(false)
  setValorTela('')
  setResultado(0)
  setACumulador(0)
}

const operacao=(oper)=>{
  if(oper == 'bs'){
    let vtela = valorTela
    vtela = vtela.substring(0,(vtela.length-1))
    setValorTela(vtela)
    setOperado(false)
    return
  }
  try{
    const r=eval(valorTela) //Calculo
    setACumulador(r)
    setResultado(r)
    setOperado(true)
  }catch{
    setResultado('ERRO')
  }

}

// ESTILOS:
const cssConteiner={
  display:'flex',
  justifyContent:'flex-start',
  alignItens:'center',
  flexDirection:'column',
  width:''
}
const cssTela={
  display:'flex',
  paddingLeft:'20px',
  paddinhRight:'20px',
  justifyContent:'center',
  alignItens:'Flex-start',
  backgroundColor:'#444',
  flexDirection:'colum',
  width:'260px'
}

const cssTelaOper={
  fontSize:'25px',
  color:'#fff',
  height:'20px',
}

const cssTelaRes={
  fontSize:'50px',
  color:'#fff',
}

const cssBtn={
  fontSize:'30px',
  height:'75px',
  width:'75px',
  padding:'20px',
  backgroundColor:'#000',
  color:'#fff',
  borderColor:'#000',
  textAlign:'center',
  outline:'none',
}

return (
  <>

  </>
)

}

export default App;
