import { useState } from 'react';
import './App.css';


function App() {

    const[form,setForm]=useState({'nome':'','curso':'','ano':''})

    const handleFormChange=(e)=>{
      if(e.target.getAttribute('name')=='fNome'){
        setForm({'nome':e.target.value,'curso':form.curso,'ano':form.ano})}

      else{if(e.target.getAttribute('name')=='fCurso'){
        setForm({'nome':form.nome,'curso':e.target.value,'ano':form.ano})
      }}



    }

  return (
    <div className="App">
        <label>Nome:</label>        
        <input onChange={(e)=>handleFormChange(e)} name='fNome' value={form.nome} type={'text'} ></input><br></br>

        <label>Curso:</label>        
        <input value={form.curso} name='fCurso' type={'text'} ></input><br></br>

        <label>Ano:</label>        
        <input value={form.ano} name='fAno' type={'text'} ></input>

        <p>Nome digitado:{form.nome} </p>
        <p>Curso digitado: {form.curso} </p>
        <p>Ano digitado: {form.ano} </p>

    </div>
  )
}

export default App;
