import { useState, useEffect } from 'react';
import Caixa from './Componentes/Caixa';
import Canal from './Componentes/Canal';
import Nota from './Componentes/Nota';
import Resultado from './Componentes/Resultado';




function App() {

const[nome,setNome]=useState()

const armazenar=(chave, valor)=>{
  localStorage.setItem(chave,valor)
}
const consultar=(chave)=>{
 alert (localStorage.getItem(chave))
}
const apagar=(chave)=>{
  localStorage.removeItem(chave)
}

return (
  <>
   <label>Digite um nome:</label>
   <input onChange={(e)=>setNome(e.target.value)} value={nome} type={'text'} ></input><br></br>
   <button onClick={armazenar('ls_nome',nome)}>Gravar</button>
   <button onClick={()=>consultar('ls_nome')}>Mostrar nome</button>
   <button onClick={()=>apagar('ls_nome')}>Apagar nome</button>
  </>
)

}

export default App;
