import React, { useState} from "react";




function App() {

  const[categoria,setcategoria]=useState=('')

const carros=[
  {categoria: 'esporte', preco:'15000', modelo:'Golf'},
  {categoria: 'esporte', preco:'12000', modelo:'Camaro'},
  {categoria: 'SUV', preco:'15000', modelo:'hrv'},
  {categoria: 'SUV', preco:'18500', modelo:'T-Cros'},
  {categoria: 'Utilitario', preco:'15000', modelo:'Hilux'},
  {categoria: 'Utilitario', preco:'40000', modelo:'Ranger'},

]

const linhas=(cat)=>{
const li=[]
carros.forEach(
  (carro)=>{
    if(carro.categoria.toUpperCase == cat.toUpperCase || cat.toUpperCase == ''){
      li.push(
        <tr>
          <td>{carro.categoria}</td>
          <td>{carro.preco}</td>
          <td>{carro.modelo}</td>
        </tr>

      )
    }
  }
)
}

const tabelaCarros=(cat)=>{
  return(
    <table  border='1'  style={{borderCollapse:'collapse'}}>
      <thead>
        <tr>
          <th>Categoria</th> <th>preço</th> <th>Modelo</th>
        </tr>
      </thead>
      <tbody>  
        {linhas(cat)}
      </tbody>

    </table>
  )
}


return (
  <>
    {tabelaCarros(categoria)}
  </>
)

}

export default App;
