import React, { useState} from "react";
import Fpeso from "./Componentes/Fpeso";
import Faltura from "./Componentes/Faltura";
import Calcular from "./Componentes/Calcular";
import Fresultado from "./Componentes/Fresultado";
import Tabela from "./Componentes/Tabela";





function App() {

  const[peso, setPeso]=useState('')
  const[altura, setAltura]=useState('')
  const[resultado,setResultado]=useState()


return (
  <>
  <Fpeso p={peso} setP={setPeso}/>
  <Faltura a={altura} setA={setAltura}/>
  <Calcular p={peso} a={altura} setR={setResultado}/>
  <p>{peso}</p>
  <Fresultado r={resultado}/>
  <Tabela />
  </>
)

}

export default App;
