import { useState } from 'react';
import Nota from './Componentes/Nota';
import Resultado from './Componentes/Resultado';


function App() {

  const[nota1, setNota1]=useState({'nota1':'0','nota2':0, nota3: 0 , nota4: 0})


return (
  <div>
    <Nota num={1} nota={nota1} setNota={setNota1}/>
    <Nota num={2} nota={nota2} setNota={setNota2}/>
    <Nota num={3} nota={nota3} setNota={setNota3}/>
    <Nota num={4} nota={nota4} setNota={setNota4}/>

    <Resultado somaDasNotas={parseFloat(nota1) + parseFloat(nota2) + parseFloat(nota3) + parseFloat(nota4)} />

  </div>

)

}

export default App;
