import React, { useState} from "react";

const calcular=()=>{
  return (
    <div>
      <button></button>

    </div>
  )

}

 const informePeso=(p,setP)=>{
    return <div>
              <label>Informe seu Peso:</label>
              <input value={p} onChange={(e)=>setP(e.target.value)} type={'number'}></input>
            </div> 

  }

  const informeAtura=(a,setA)=>{
    return <div>
              <label>Informe sua Altura:</label>
              <input value={a}  onChange={(e)=>setA(e.target.value)} type={'number'}></input>
            </div> 

  }

 const tabelaIMC =()=>{
  return(

  <table border={'1'} style={{borderCollapse:'collapse'}}>
    <thead>
      <tr>
        <th>Classificação</th>
        <th>IMC</th>
      </tr>
    </thead>
    <tbody>
      <tr>
        <td>Abaixo do peso</td>
        <td>Abaixo de 18,5</td>
      </tr>
      <tr>
        <td>Peso Normal</td>
        <td>Entre 18,5 e 24,9</td>
      </tr>
      <tr>
        <td>Sobrepeso</td>
        <td>Entre 25 e 29,9</td>
      </tr>
      <tr>
        <td>Obesidade Grau I</td>
        <td>Entre 30 e 34,9</td>
      </tr>
      <tr>
        <td>Obesidade de Grau II</td>
        <td>Entre 35 e 39,9</td>
      </tr>
      <tr>
        <td>Obesidade de Grau III ou Morbida.</td>
        <td>Abaixo de 18,5</td>
      </tr>
    </tbody>
 </table>

  )
 }

function App() {

 const[peso, setPeso]=useState()
 const[altura, setAltura]=useState()



return (
  <>
  {informePeso(peso,setPeso)}
  <br></br>
  {informeAtura(altura,setAltura)}
  {tabelaIMC()}
  </>
)

}

export default App;
